tinymce.PluginManager.add('escoimage', function(editor, url){
    editor.addButton('insertimage', {
        text: 'My Photos',
        tooltip: 'Add image from your escoNet photos',
        icon: false,
        onclick: function() {
            editor.windowManager.open({
                title: 'Select from your photostream',
                url: 'http://www.esco.net/api/tinymce-photos.php',
                width: '745',
                height: '500'
            });
        }
    });
});